custom_css:
/* Example CSS for your font: msyh */

/* Apply to all text */
:root * {
	--font-default: 'msyh';
	--default-font: 'msyh';
	--font-family-editor: 'msyh';
	--font-interface-override: 'msyh';
	--font-text-override: 'msyh';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: 'msyh' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: 'msyh' !important;
}
